import React, { useState } from 'react';
import { Accordion, Card, Button, Table } from 'react-bootstrap';

function Question({ summaryText, fullText, isOpen }) {
  return <p>{isOpen ? fullText : summaryText}</p>;
}

function Response({ summaryText, fullText, isOpen }) {
  return <p>{isOpen ? fullText : summaryText}</p>;
}

function Row({ question, response1, response2 }) {
    const [isOpen, setIsOpen] = useState(false);
  
    return (
      <Card>
        <Card.Header>
          <Accordion.Toggle as={Button} variant="link" eventKey="0" onClick={() => setIsOpen(!isOpen)}>
            <Table borderless size="sm">
              <tbody>
                <tr>
                  <td><Question summaryText={question.summaryText} fullText={question.fullText} isOpen={isOpen} /></td>
                  <td><Response summaryText={response1.summaryText} fullText={response1.fullText} isOpen={isOpen} /></td>
                  <td><Response summaryText={response2.summaryText} fullText={response2.fullText} isOpen={isOpen} /></td>
                </tr>
              </tbody>
            </Table>
          </Accordion.Toggle>
        </Card.Header>
        <Accordion.Collapse eventKey="0">
          <Card.Body>
            {/* You can add more detailed information here if needed */}
          </Card.Body>
        </Accordion.Collapse>
      </Card>
    );
  }
  

function ComparisonTable({ data }) {
  return (
    <Accordion defaultActiveKey="0">
      {data.map((row, index) => (
        <Row key={index} question={row.question} response1={row.response1} response2={row.response2} />
      ))}
    </Accordion>
  );
}

export default ComparisonTable;
