// Row.js
import React, { useState } from 'react';
import { TableCell, TableRow, Collapse, IconButton } from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

function Row({ question, response1, response2 }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <TableRow>
        <TableCell>
          <IconButton size="small" onClick={() => setIsOpen(!isOpen)}>
            <ExpandMoreIcon />
          </IconButton>
        </TableCell>
        <TableCell>{isOpen ? question.fullText : question.summaryText}</TableCell>
        <TableCell>{isOpen ? response1.fullText : response1.summaryText}</TableCell>
        <TableCell>{isOpen ? response2.fullText : response2.summaryText}</TableCell>
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={isOpen} timeout="auto" unmountOnExit>
            <TableCell>{question.fullText}</TableCell>
            <TableCell>{response1.fullText}</TableCell>
            <TableCell>{response2.fullText}</TableCell>
          </Collapse>
        </TableCell>
      </TableRow>
    </>
  );
}

export default Row;
